$('body').on('click','.navbar-toggler',function () {
    $(this).addClass('toggled');
    $('html').addClass('nav-open');
    $('#close-layer').addClass('visible').addClass('close-layer');
});
$('body').on('click','#close-layer,.close-nav',function () {
    $('.navbar-toggler').removeClass('toggled');
    $('html').removeClass('nav-open');
    $('#close-layer').removeClass('visible').removeClass('close-layer');
});